mapper.select:
select * from (select n.*, rownum rn from (select * from note where nstatus in (0,1)
and (nid like '%${options}%' or ntitle like '%${options}%' or ncontent like '%${options}%' or ntimes like '%${options}%' )
) n where ${pageSize}* ${currPage} >= rownum) where rn>(${currPage}-1)*${pageSize}

select * from (select n.*, rownum rn from (select * from note where
tid like '${options}%'
) n where ${pageSize}* ${currPage} >= rownum) where rn>(${currPage}-1)*${pageSize}

select * from (select n.*, rownum rn from (select * from note where nstatus in (0,1) and nstates in (0,1)
and userid like '${options}%'
) n where ${pageSize}* ${currPage} >= rownum) where rn>(${currPage}-1)*${pageSize}
