mapper.select:
select * from (select n.*, rownum rn from (select o.*,u.uname uname,t.tname tname from note o,users u,types t where o.nstatus in (0,1)
and (o.nid like '%${options}%' or o.ntitle like '%${options}%' or o.ncontent like '%${options}%' or o.ntimes like '%${options}%' )
and o.userid=u.userid and o.tid = t.tid ) n where ${pageSize}* ${currPage} >= rownum) where rn>(${currPage}-1)*${pageSize}

select count(1) total,ceil(count(1)/${pageSize})
totalPage,${pageSize}
pageSize ,${currPage} currPage from note where nstatus=1
